mapper.select:
select b.* from( select a.*, row_number() over(order by a.POP_NO desc) rnum from( select POP_NO, POP_TITLE, POP_CONTENT, POP_GOAL, POP_STARTDATE, POP_ENDDATE, POP_IMG, POP_APPROVAL, POP_REJECT, PT_NO from POPUP where 1=1
order by POP_NO desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <=#{endRow}

mapper.update:

UPDATE POPUP SET 
	pop_approval = '게시중'
WHERE POP_NO =  #{popNo}
