mapper.insert:

	SELECT SEQ_CALENDAR.NEXTVAL FROM DUAL

INSERT INTO CALENDAR(
	CAL_NO
  , CAL_TITLE
  , CAL_STARTDATE
  , CAL_ENDDATE
  , CAL_TYPE
  , CAL_DIV
  , EMP_NO
)VALUES(
	#{calNo}
  , #{calTitle}
  , #{calStartdate}
  , #{calEnddate}
  , #{calType}
  , #{calDiv}
  , #{empNo}
)

mapper.update:
  		UPDATE CALENDAR
  			SET 
  				CAL_TITLE = #{calTitle}
  			  , CAL_STARTDATE = #{calStartdate}
  			  , CAL_ENDDATE = #{calEnddate}
  			  , CAL_TYPE = #{calType}
  			  , CAL_DIV = #{calDiv}
			WHERE CAL_NO = #{calNo}
			AND EMP_NO = #{empNo}

mapper.delete:
DELETE FROM CALENDAR
WHERE CAL_NO = #{calNo}
AND EMP_NO = #{empNo}
