mapper.select:
select cus_no, cus_name, cus_id, cus_add, cus_add2, cus_postcode, cus_gender, cus_tel, cus_email, cus_birth
from customer
where cus_id = #{cusId}

SELECT b.* FROM ( SELECT a.*, ROW_NUMBER() OVER (ORDER BY a.QNA_NO DESC) rnum FROM ( SELECT q.QNA_NO, q.CUS_NO, q.EMP_NO, q.QNA_TITLE, q.QNA_CONTENT, q.QNA_ANS, q.QNA_FILECODE, q.QNA_COUNT, q.QNA_DELYN, q.QNA_DATE FROM qna q, customer c WHERE q.qna_delyn = 'N' and q.cus_no = c.cus_no and c.cus_no = ( select cus_no from customer where cus_id = #{cusId})
) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.update:
update ticketing
set 
	tk_passport = #{tkPassport},
	tk_engfirst = #{tkEngfirst},
	tk_lastname = #{tkLastname}
where tk_Group = #{tkGroup}
