mapper.select:
select b.*, count(*) over() as total_cnt from ( select a.*, row_number() over(order by a.NO_NO desc) rnum from ( select NO_NO, NO_TITLE, NO_CATEGORY, NO_CONTENT, NO_CODE, NO_FILECODE, N.EMP_NO, NO_COUNT, NO_DELYN, EMP_NAME, NO_DATE from NOTICE N join EMPLOYEE E ON N.EMP_NO = E.EMP_NO where N.NO_CODE = 'NTC01' and N.NO_DELYN = 'N'
order by N.NO_NO desc ) a ) b
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:
select SEQ_FILE.nextval from dual
insert into FILE_DATA( FILE_NO, FILE_OWNER, FILE_SAVEPATH, FILE_SIZE, FILE_MIME, FILE_REALNAME, FILE_SAVENAME, FILE_SAVEDATE, FILE_DELYN, FILE_DOWNCOUNT, FILE_FANCYSIZE )VALUES( #{fileNo}, #{fileOwner}, #{fileSavepath}, #{fileSize}, #{fileMime}, #{fileRealname}, #{fileSavename}, sysdate, 'N', 0, #{fileFancysize}
)
