mapper.select:

  		select
  			b.*
  		from (
	  		select
	  			a.*, row_number() over (order by a.emp_no desc) rnum
	  		from (
		  		select
		  			emp_no, emp_name, emp_id, emp_dept, emp_position, emp_approval, emp_del, emp_startdate, emp_carmodel, emp_carnum, emp_image
		  		from employee
		  		where 1=1

		  		order by emp_no desc
		  	) a
		  ) b

		  	where b.rnum >= #{startRow} and b.rnum <= #{endRow}

  		select emp_name, emp_id, emp_dept, emp_birth, emp_email, emp_tel, emp_addr, emp_addr2, emp_postcode, emp_position, emp_approval, emp_reject, emp_del, emp_startdate, emp_carmodel, emp_carnum
			from employee
		where emp_id = #{empId}

mapper.update:
update employee
set
	emp_dept = #{empDept}
where emp_id = #{empId}

update employee
set
	emp_approval = 'R',
	emp_reject = #{empReject}
where emp_Id = #{empId}
