mapper.select:
SELECT b.* FROM ( SELECT a.*, ROW_NUMBER() OVER (ORDER BY a.no_no DESC) rnum FROM ( select n.no_no, n.no_category, n.no_title, n.no_content, n.emp_no, e.emp_dept, n.no_filecode, n.no_count, n.no_delyn, n.no_date from notice n left join employee e ON n.emp_no = e.emp_no where n.no_code = 'NTC03' and n.no_delyn = 'N' order by n.no_no desc
) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

