mapper.select:
SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.pt_no asc) rnum FROM ( SELECT CDC.COMD_NAME, PT.PT_NO, PT.PT_NAME, PT.PT_BUSNUM, PT_CODE, PT_APPROVAL, PT_OPENDATE, PT_CEO, PT_MGRTEL FROM PARTNER PT LEFT OUTER JOIN COMMON_DETAIL_CODE CDC ON(PT.PT_CODE = CDC.COMD_CODE) WHERE 1=1
ORDER BY PT.PT_NO ASC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.insert:

			select SEQ_PARTNER.NEXTVAL from dual

