 		)VALUES(
 			#{ptNo}, #{ptCode}, #{ptBusnum}, #{ptName}, #{ptCeo}, #{ptOpendate}, #{ptPostcode}, #{ptAdd}, #{ptAdd2}, #{ptMgrname}, #{ptMgremail}, #{ptMgrtel}, 'H', #{ptFilecode}, #{ptImage} 
 		)

mapper.update:
UPDATE PARTNER
SET PT_APPROVAL = #{ptApproval},
	PT_REJECT = #{ptReject}
