mapper.select:
SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.in_no desc) rnum FROM ( SELECT INS.IN_NO, INS.PT_NO, INS.IN_STARTDATE, INS.IN_ENDDATE, INS.IN_GOAL, INS.IN_STAFF, INS.IN_PLACE, INS.IN_STATE, INS.IN_FILECODE, INS.IN_TITLE, INS.IN_CONTENT, INS.REPORT_DATE FROM INSPECTION_SCHEDULE INS LEFT OUTER JOIN PARTNER PT ON(INS.PT_NO = PT.PT_NO) WHERE 1=1 AND PT.PT_NO = #{ptNo} AND IN_STATE = 'Y'
ORDER BY INS.IN_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.in_no desc) rnum FROM ( SELECT INS.IN_NO, INS.PT_NO, INS.IN_STARTDATE, INS.IN_ENDDATE, INS.IN_GOAL, INS.IN_STAFF, INS.IN_PLACE, INS.IN_STATE, INS.IN_FILECODE, INS.IN_TITLE, INS.IN_CONTENT, INS.REPORT_DATE FROM INSPECTION_SCHEDULE INS LEFT OUTER JOIN PARTNER PT ON(INS.PT_NO = PT.PT_NO) WHERE 1=1 AND IN_STATE = 'Y'
ORDER BY INS.IN_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.insert:
select SEQ_FILE.nextval from dual
insert into FILE_DATA( FILE_NO, FILE_OWNER, FILE_SAVEPATH, FILE_SIZE, FILE_MIME, FILE_REALNAME, FILE_SAVENAME, FILE_SAVEDATE, FILE_DELYN, FILE_DOWNCOUNT, FILE_FANCYSIZE )VALUES( #{fileNo}, #{fileOwner}, #{fileSavepath}, #{fileSize}, #{fileMime}, #{fileRealname}, #{fileSavename}, sysdate, 'N', 0, #{fileFancysize}
)


			select SEQ_INSPECTION_SCHEDULE.NEXTVAL from dual

