mapper.select:
SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.in_no desc) rnum FROM ( SELECT INS.IN_NO, INS.PT_NO, INS.IN_GOAL, INS.IN_PLACE, INS.IN_STATE, INS.IN_RECEPTION, PT.PT_NAME FROM INSPECTION_SCHEDULE INS LEFT OUTER JOIN PARTNER PT ON(INS.PT_NO = PT.PT_NO) WHERE 1=1 AND PT.PT_NO = #{ptNo}
ORDER BY INS.IN_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.in_no desc) rnum FROM ( SELECT INS.IN_NO, INS.PT_NO, INS.IN_GOAL, INS.IN_PLACE, INS.IN_STATE, INS.IN_RECEPTION, PT.PT_NAME FROM INSPECTION_SCHEDULE INS LEFT OUTER JOIN PARTNER PT ON(INS.PT_NO = PT.PT_NO) WHERE 1=1
ORDER BY INS.IN_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}
