mapper.select:
SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.qna_no desc) rnum FROM ( SELECT CDC.COMD_NAME, QNA.QNA_NO, QNA.QNA_TITLE, QNA.QNA_CONTENT, QNA.QNA_DATE, QNA.QNA_STATE, QNA.QUE_FILECODE, QNA.QNA_DELYN, PT.PT_NAME FROM PARTNER_QNA QNA LEFT OUTER JOIN PARTNER PT ON(QNA.PT_NO = PT.PT_NO) LEFT OUTER JOIN COMMON_DETAIL_CODE CDC ON(PT.PT_CODE = CDC.COMD_CODE) WHERE 1=1 AND QNA_DELYN = 'N'
ORDER BY QNA.QNA_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

SELECT b.* FROM ( SELECT a.*, row_number() over(order by a.qna_no desc) rnum FROM ( SELECT CDC.COMD_NAME, QNA.QNA_NO, QNA.QNA_TITLE, QNA.QNA_CONTENT, QNA.QNA_DATE, QNA.QNA_STATE, QNA.QUE_FILECODE, QNA.QNA_DELYN, PT.PT_NAME FROM PARTNER_QNA QNA LEFT OUTER JOIN PARTNER PT ON(QNA.PT_NO = PT.PT_NO) LEFT OUTER JOIN COMMON_DETAIL_CODE CDC ON(PT.PT_CODE = CDC.COMD_CODE) WHERE 1=1
ORDER BY QNA.QNA_NO DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.insert:

			select SEQ_PARTNER_QNA.NEXTVAL from dual

 		INSERT INTO PARTNER_QNA(
 			QNA_NO, PT_NO, QNA_TITLE, QNA_CONTENT, QUE_FILECODE, QNA_DATE, QNA_STATE, QNA_COUNT, QNA_DELYN 
 		)VALUES(
 			#{qnaNo}, #{ptNo}, #{qnaTitle}, #{qnaContent}, #{queFilecode}, SYSDATE, 'N', 0, 'N'
 		)

select SEQ_FILE.nextval from dual
insert into FILE_DATA( FILE_NO, FILE_OWNER, FILE_SAVEPATH, FILE_SIZE, FILE_MIME, FILE_REALNAME, FILE_SAVENAME, FILE_SAVEDATE, FILE_DELYN, FILE_DOWNCOUNT, FILE_FANCYSIZE )VALUES( #{fileNo}, #{fileOwner}, #{fileSavepath}, #{fileSize}, #{fileMime}, #{fileRealname}, #{fileSavename}, sysdate, 'N', 0, #{fileFancysize}
)


			select SEQ_PARTNER_FAQ.NEXTVAL from dual

 		INSERT INTO PARTNER_FAQ(
 			FAQ_NO, FAQ_TITLE, FAQ_ANS, FAQ_CATEGORY, FAQ_DELYN 
 		)VALUES(
 			#{faqNo}, #{faqTitle}, #{faqAns}, #{faqCategory}, 'N'
 		)
